import{_ as i,o as e,c as d,R as t}from"./chunks/framework.bQUviISV.js";const v=JSON.parse('{"title":"八字短句集","description":"","frontmatter":{},"headers":[],"relativePath":"Life/八字短句集.md","filePath":"Life/八字短句集.md","lastUpdated":1704342415000}'),r={name:"Life/八字短句集.md"},o=t('<h1 id="八字短句集" tabindex="-1">八字短句集 <a class="header-anchor" href="#八字短句集" aria-label="Permalink to &quot;八字短句集&quot;">​</a></h1><div style="text-align:center;"><div style="border:2px solid orange;background-color:white;padding:20px;border-radius:10px;font-family:&#39;楷体&#39;, KaiTi, serif;font-size:20px;"> 椿萱并茂, 棠棣同馨 </div></div><p>【译文】指椿树和萱草都茂盛, 表示祝家中的亲人身体健康且一切顺利.</p><div style="text-align:center;"><div style="border:2px solid green;background-color:white;padding:20px;border-radius:10px;font-family:&#39;楷体&#39;, KaiTi, serif;font-size:20px;"> 岁月缱绻, 葳蕤生香 </div></div><p>【译文】岁月情长, 如同香草的气息温柔缠绵. 草木繁盛, 美好的回忆余韵悠长. 愿我们一切安好, 繁花似锦, 未来可期.</p><div style="text-align:center;"><div style="border:2px solid yellow;background-color:white;padding:20px;border-radius:10px;font-family:&#39;楷体&#39;, KaiTi, serif;font-size:20px;"> 不啻微茫, 造炬成阳 </div></div><p>【译文】你逆光而来, 配得上世间所有的美好, 光终究会撒在你身上, 你也会灿烂成光.</p><div style="text-align:center;"><div style="border:2px solid blue;background-color:white;padding:20px;border-radius:10px;font-family:&#39;楷体&#39;, KaiTi, serif;font-size:20px;"> 桑榆非晚, 柠月如风 </div></div><p>【译文】珍惜将来的日子还不算晚. 但青春岁月像风一样迅疾, 转瞬即逝. 愿我们珍惜时间, 坐看岁月漫漫, 拥抱平平安安.</p><div style="text-align:center;"><div style="border:2px solid pink;background-color:white;padding:20px;border-radius:10px;font-family:&#39;楷体&#39;, KaiTi, serif;font-size:20px;"> 葳蕤繁祉, 延彼遐龄 </div></div><p>【译文】对别人发自内心的祝福, 祝福他人家庭美满, 事业有成, 人丁兴旺, 健康长寿, 福寿绵长.</p><div style="text-align:center;"><div style="border:2px solid red;background-color:white;padding:20px;border-radius:10px;font-family:&#39;楷体&#39;, KaiTi, serif;font-size:20px;"> 遥叩芳辰, 生辰吉乐 </div></div><p>【译文】生日祝福语, 愿你所求皆所愿, 所行化坦途, 平安喜乐, 万事胜意, 生日快乐!</p><div style="text-align:center;"><div style="border:2px solid purple;background-color:white;padding:20px;border-radius:10px;font-family:&#39;楷体&#39;, KaiTi, serif;font-size:20px;"> 顺遂无虞, 皆得所愿 </div></div><p>【译文】希望一切平安, 一切顺顺利利, 每个人都能实现自己所想.</p>',15),a=[o];function n(s,p,l,c,f,x){return e(),d("div",null,a)}const b=i(r,[["render",n]]);export{v as __pageData,b as default};
